import React, { useEffect } from 'react';
import { useDispatch } from 'react-redux'; 
import { mousedOver, mousedOffDashboard } from '../../actions';

// Okay, so goal going forward: is to create a mousedOver action that accepts an argument you pass in.


export default function DashboardButton() {
    const dispatch = useDispatch();
    let dashboard = "dashboard";
    useEffect(() => {
        let dashboardButton = document.querySelector(".dashboard-button")
        dashboardButton.addEventListener("mouseover", () => {
        dashboardButton.classList.add("dashboard-hovered")
        dispatch(mousedOver(dashboard))})
        dashboardButton.addEventListener("mouseleave", () => {
        dashboardButton.classList.remove("dashboard-hovered")
        dispatch(mousedOffDashboard())
    })

      });
    return (
        <div className="dashboard-button-container">
        <h1 className="dashboard-button">Dashboard</h1>
        <style jsx>
        {

`
.dashboard-button-container {
    display: flex;
    align-items: center;
    justify-content: center;
}

.dashboard-button {
    font-family: Montserrat;
    font-size: 25px;
    padding-left: 22px;
    position: relative;
    right: 164px;
    bottom: 2020px;
    background-color: transparent;
    color: white;
    border:black;
    background: linear-gradient(to bottom, rgb(212,212,245), rgb(47, 164, 231));
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    transition: transform 1s;
    transition-timing-function: ease-out;
}

.dashboard-hovered {
    cursor: none;
    transform: scale(1.23);
}
`
        }


        </style>
        </div>
    );
  }
